mapper.select:
select , , if((all_salary - tax + reward + punishment) > 0, (all_salary - tax + reward + punishment), 0) as wages_payable from ( select emp.`name` as employee_name, emp.work_id as work_id, dep.`name` as department_name, jl.`name` as job_level_name, sal.*, if(year(ads.create_date) = year(#{date}) and month(ads.create_date) = month(#{date}), if(ads.salary > 0, ads.salary, 0), 0) as reward, if(year(ads.create_date) = year(#{date}) and month(ads.create_date) = month(#{date}), if(ads.salary < 0, ads.salary, 0), 0) as punishment, case when all_salary <= 3000 then all_salary * 0.03 when all_salary <= 12000 then all_salary * 0.10 when all_salary <= 25000 then all_salary * 0.20 when all_salary <= 35000 then all_salary * 0.25 when all_salary <= 55000 then all_salary * 0.30 when all_salary <= 80000 then all_salary * 0.35 when all_salary > 80000 then all_salary * 0.45 end as tax from employee as emp join employee_salary_merge as ems on ems.employee_id = emp.id join salary as sal on sal.id = ems.salary_id join department as dep on dep.id = emp.department_id join job_level as jl on jl.id = emp.job_level_id left join adjust_salary as ads on ads.employee_id = emp.id
work_id = #{workId}
) as t
